import React, { useState } from 'react';
import Header from './Header';
import Body from './Body';

function App() {
  const [items, setItems] = useState([
    { id: 1, name: 'Товар 1', checked: false },
    { id: 2, name: 'Товар 2', checked: false },
    { id: 3, name: 'Товар 3', checked: false },
    // Додайте більше товарів за потребою
  ]);

  // Функція для оновлення стану елементів при кліку на checkbox
  const handleCheckboxChange = (itemId) => {
    const updatedItems = items.map((item) =>
      item.id === itemId ? { ...item, checked: !item.checked } : item
    );
    setItems(updatedItems);
  };

  return (
    <div className="App">
      <Header />
      <Body items={items} handleCheckboxChange={handleCheckboxChange} />
      <Footer />
    </div>
  );
}

export default App;
